package TypeCasting;

public class Type1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		
		long d=a;
		
		System.out.println(d);
		
		float f=10.5f;
		double g=f;
		
		System.out.println(g);
		
		/// downcasting
		
		long ls=10000;
		int is=(int)ls;
		
		double dd=10.54543;
		
		float ff=(float)dd;
		
		///
		int a1=10;
		
		double d1=(double)a1;
		
		System.out.println(d1);
		
		///
		
		double d2=10.5;
		
		int i2=(int)d2;
		
		System.out.println(i2);
		
		
		byte bb=10;
		
		long ld=bb;
		
		double dds=10.45656;
		
		byte bbs=(byte)dds;
		
		
		
		
		

	}

}
